const ADD_COMMENT = 'ADD_COMMENT';
const CHANGE_LIKES_AND_DISLIKES = 'CHANGE_LIKES_AND_DISLIKES';

let intialState = {comments: [{id:1, staticInfo: {content:'t1', place:'Tokyo Revengers', author:'user1'}, dynamicInfo:{dislikes: 0, likes: 0,}},
                              {id:2, staticInfo: {content:'t2', place:'Tokyo Revengers', author:'user3'}, dynamicInfo:{dislikes: 0, likes: 0,}},
                              {id:3, staticInfo: {content:'t3', place:'It', author:'user1'}, dynamicInfo:{dislikes: 0, likes: 0,}},
                              {id:4, staticInfo: {content:'t4', place:'Tokyo Revengers', author:'user2'}, dynamicInfo:{dislikes: 0, likes: 0,}}]};

const commentsReducer = (state = intialState, action) => {
  switch (action.type) {
    case ADD_COMMENT: {
      let newComment = {id:state.comments[-1], staticInfo: {content:'t1', place:'Tokyo Revengers', author:'user1'}, dynamicInfo:{dislikes: 0, likes: 0,}};

      let stateCopy = {...state}

      stateCopy.comments = [...state.comments]
      stateCopy.comments.push(newComment);
      return stateCopy;
    }

    default:
      return state;
  }
};

export default commentsReducer;
