function Fiction(id, fictionInfo, type, overAllRating) {
  this.id = id;
  this.info = fictionInfo;
  this.type = type;
  this.overAllRating = overAllRating;
}

let fiction0 = new Fiction(0, {coverPhoto:'', name:'Overgeared', author:'idk', genre:'lightnovel', releaseDate:'12.04.2015', description:'description'}, 'book', 9)
let fiction1 = new Fiction(1, {coverPhoto:'', name:'Solo-Leveling', author:'idk', genre:'lightnovel', releaseDate:'3.10.2013', description:'description'}, 'book', 7)
let fiction2 = new Fiction(2, {coverPhoto:'', name:'It', director:'idk', genre:'horror', releaseDate:'3.10.2013', description:'description'}, 'film', 6)
let fiction3 = new Fiction(3, {coverPhoto:'', name:'It', author:'idk', genre:'horror', releaseDate:'3.10.2013', description:'description'}, 'book', 5)

let dbFictions = {fictions:[fiction0, fiction1, fiction2, fiction3]}

export const getFictionById = (fiction_id)=>{
  //returns fiction whose id was mentioned
  for (var i = 0; i <dbFictions.fictions.length; i++) {
    if(dbFictions.fictions[i].id == fiction_id){
      return dbFictions.fictions[i]
    }
  }
}

export const getAllFilms = ()=>{
  //returns all fictions whose type is film
  let films = []
  for (var i = 0; i <dbFictions.fictions.length; i++) {
    if(dbFictions.fictions[i].type == 'film'){
      films.push(dbFictions.fictions[i])
    }
  }
  return films
}

export const getAllBooks = ()=>{
  //returns all fictions whose type is film
  let books = []
  for (var i = 0; i <dbFictions.fictions.length; i++) {
    if(dbFictions.fictions[i].type == 'book'){
      books.push(dbFictions.fictions[i])
    }
  }
  return books
}
