import logo from './logo.svg';
import './App.css';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import ConstantMenu from './components/ConstantMenu/constantMenu.jsx';
import RegistrationPage from './components/RegistrationPage/registrationPage.jsx'
import ProfilePage from './components/ProfilePage/profile.jsx';
import FilmCatalogPage from './components/FilmCatalogPage/filmCatalogPage.jsx';
import BookCatalogPage from './components/BookCatalogPage/bookCatalogPage.jsx';
import ChosenBook from './components/ChosenBook/chosenBook.jsx';
import ChosenFilm from './components/ChosenFilm/chosenFilm.jsx';

const App = (props) => {

  let filmLinks = props.state.filmsR.films.map((film)=>{
      let link = '/filmCatalog/' + film.info.name.replace(/\s+/g, '_');
     return (<Route path={link} element={<ChosenFilm info={film.info} rating={film.overallRating} comments={props.state.commentsR.comments} dispatch={props.dispatch}/>}/>)
   });
  let bookLinks = props.state.booksR.films.map((book)=>{
       let link = '/bookCatalog/' + book.info.name.replace(/\s+/g, '_');
      return (<Route path={link} element={<ChosenBook info={film.info} rating={film.overallRating} comments={props.state.commentsR.comments} dispatch={props.dispatch}/>}/>)
    });


  return (
    <Router>
      <div className="App">
        <ConstantMenu />
        <div className="app-wrapper-content">
          <Routes>
            <Route path='/profile' element={<ProfilePage comments={props.state.commentsR.comments}/>}/>
            <Route path='/registrate' element={<RegistrationPage />}/>
            <Route path='/filmCatalog' element={<FilmCatalogPage filmsList={props.state.filmsR}/>}/>
            <Route path='/bookCatalog' element={<BookCatalogPage/>}/>
            {filmLinks}
            {bookLinks}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
