import s from './comment.module.css'
import {changeLikesAndDislikesActionCreator} from './../../redux/reducers/commentsR'
import {likeOrDislikeCommentActionCreator} from './../../redux/reducers/usersR'

const CommentBlock = (props)=>{

  let like = ()=>{
    props.dispatch(likeOrDislikeCommentActionCreator(props.comment, 'like'))
    props.dispatch(changeLikesAndDislikesActionCreator(props.id, 'like'))
  }
  let dislike = ()=>{
    props.dispatch(likeOrDislikeCommentActionCreator(props.comment, 'dislike'))
    props.dispatch(changeLikesAndDislikesActionCreator(props.id, 'dislike'))
  }

  let rating = parseInt(props.dynamicInfo.likes) - parseInt(props.dynamicInfo.dislikes)
  return (
    <div className={s.commentBlock}>
      <div className={s.names}>
        <div className={s.user}>{props.staticInfo.author}</div>
        <div className={s.placeName} >{props.staticInfo.place}</div>
      </div>
      <div className={s.commentText}>{props.staticInfo.content}</div>
      <div className={s.likesAndDislikes}>
        <button onClick={like}>Like!</button>
        <div>{rating}</div>
        <button onClick={dislike}>Dislike!</button>
      </div>
    </div>
  )
}

export default CommentBlock;
