import logo from './logo.svg';
import './App.css';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import ConstantMenu from './components/ConstantMenu/constantMenu.jsx';
import RegistrationPage from './components/RegistrationPage/registrationPage.jsx'
import ProfilePage from './components/ProfilePage/profile.jsx';
import FilmCatalogPage from './components/FilmCatalogPage/filmCatalogPage.jsx';
import BookCatalogPage from './components/BookCatalogPage/bookCatalogPage.jsx';
import ChosenBook from './components/ChosenBook/chosenBook.jsx';
import ChosenFilm from './components/ChosenFilm/chosenFilm.jsx';

const App = (props) => {

  let filmLinks = props.state.filmsR.films.map((film)=>{
      let link = '/filmCatalog/' + film.info.name.replace(/\s+/g, '_');
      console.log(link)
     return (<Route path={link} element={<ChosenFilm info={film.info} rating={film.overallRating} comments={film.comments}/>}/>)
   });


  return (
    <Router>
      <div className="App">
        <ConstantMenu />
        <div className="app-wrapper-content">
          <Routes>
            <Route path='/profile' element={<ProfilePage comments={props.state.filmsR.films[0].comments}/>}/>
            <Route path='/registrate' element={<RegistrationPage />}/>
            <Route path='/filmCatalog' element={<FilmCatalogPage filmsList={props.state.filmsR}/>}/>
            <Route path='/bookCatalog' element={<BookCatalogPage/>}/>
            <Route path='/bookCatalog/classroom_of_the_elite' element={<ChosenBook author="Kinugasa" genre="Lightnovel" aboutBook="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam a pulvinar elit. Sed eget dui dolor. Pellentesque facilisis lacus eget tortor fermentum viverra. Vestibulum placerat ligula non ligula eleifend tincidunt. Vivamus molestie massa sed arcu viverra posuere. Proin nec efficitur risus. Vivamus faucibus lacinia mauris sed efficitur. Donec a suscipit dui, at tristique mi. Donec eu tortor quis justo feugiat maximus in eu tellus. Aenean quis libero eget diam fermentum porta at id lacus. Donec a ipsum id dolor rutrum laoreet. Vivamus nisl nisl, sollicitudin in nisi ut, hendrerit accumsan diam. Maecenas non magna maximus, vulputate arcu nec, placerat ipsum."/>}/>
            {filmLinks}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
