const LOG_IN = 'LOG_IN';
const SIGN_UP = 'SIGN-UP;'

let intialState = {status:'-',users:[{name:'John', email:'eee@gmail.com', password:'12345'}, {name:'Paul', email:'aaa@gmail.com', password:'12389'}]};

const usersReducer = (state = intialState, action) => {
  switch (action) {
    case LOG_IN:{
      for (let i=0; i<state.users;i++){
        if ((state.users[i].name == action.name) && (state.users[i].password == action.password)){
          let stateCopy = {...state}
          stateCopy.status = '+';
          return stateCopy;
        }
      }
      return state;
    }
    case SIGN_UP:{
      let stateCopy = {...state}
      stateCopy.status = '+';
      return stateCopy;
    }

    default:
      return state;
  }
};

export default usersReducer;

export const tryLogIn = (username, password) => (
  {type: LOG_IN, username:username, password:password});
export const trySignUp = (username, password, email) => (
  {type: SIGN_UP, username:username, password:password, email:email});
