import ProfilePage from './profile.jsx';
import {getAllUsers, getUsersFriends, getUserById} from './../../DataBase/Users.js'
import {getFictionById} from './../../DataBase/Fictions.js'
import {getUsersComments} from './../../DataBase/Comments.js'
import {getLoggedUserId, getLoggedInStatus} from './../../LocalInfo/localInfo.js'
import {Routes, Route} from "react-router-dom";

const ProfilePagesContainer = (props) =>{


  let userLinks = getAllUsers().map((user)=>{
    let link = '/profile/' + user.id;
    let type = 'nobodyIsLoggedIn'
    if (getLoggedInStatus()){
      if (user.id == getLoggedUserId()) {
        type = 'loggedUsersProfile';
      } else{
        type = 'notLoggedUsersProfile';
      }
    }

    let planned_films = []
    let watched_films = []
    let planned_books = []
    let read_books = []
    for (let i = 0; i < user.markedFiction; i++){
      if (user.markedFiction[i].status == 'completed'){
        let f = getFictionById(user.markedFiction[i].fiction_id)
        if (f.type == 'book'){
          read_books.push({id:f.id, info:f.info})
        } else if (f.type == 'film') {
          watched_films.push({id:f.id, info:f.info})
        }
      } else if (user.markedFiction[i].status == 'planned') {
        let f = getFictionById(user.markedFiction[i].fiction_id)
        if (f.type == 'book'){
          planned_books.push({id:f.id, info:f.info})
        } else if (f.type == 'film') {
          planned_films.push({id:f.id, info:f.info})
        }
      }
    }
    let fictions_info = {planned_films: planned_films, planned_books:planned_books,
                         watched_films: watched_films, read_books: read_books}


    return (<div><Route path={link} element={<ProfilePage dispatch={props.dispatch}
                              fictions_info={fictions_info} info={user.usersInfo}
                              friends={getUsersFriends(user.id)}
                              comments={getUsersComments(user.id)} type={type}/>}/>
            </div>)
  })




  return (
    <Routes>
      {userLinks}
    </Routes>
  );
}

export default ProfilePagesContainer;
