import s from './bookElem.module.css'
import {NavLink} from "react-router-dom";

const BookElem = (props) =>{
  let bookLink = '/bookCatalog/' + props.info.info.name.replace(/\s+/g, '_');
  return (
    <div className={s.bookElem}>
      <div className={s.bookCover}><img src={props.info.info.bookCover} /></div>
      <div className={s.details}>
        <h3>{props.info.info.name}</h3>
        <p><b>Genre:</b> {props.info.info.genre}</p>
        <p>{props.info.info.aboutBook}</p>
      </div>
      <div className={s.rating}>Rating:<br />{props.info.overallRating}</div>
      <div className={s.link_button}><NavLink to={bookLink}><button>Look!</button></NavLink></div>
    </div>
  )
}

export default BookElem;
