import RegistrationPage from './registrationPage.jsx'

import { useNavigate } from 'react-router-dom'

import {tryLogIn, trySignUp} from './../../redux/reducers/registrationReducer';
import { getLoggedUserId, setLoggedUserId } from './../../LocalInfo/localInfo.js'

const RegistrationPageContainer = (props) =>{

  let navigate = useNavigate()

  let link =  '/registration';
  if (typeof getLoggedUserId() == 'number'){
    link = '/profile/' + getLoggedUserId()
  }

  async function logInFunc (username, password) {
    async function getId(){
      const response = await fetch(`http://localhost:5000/user/checkIfUsersDataIsRight/${name}/${pass}`);

      if (!response.ok) {
         const message = `An error occurred at registpage: ${response.statusText}`;
          window.alert(message);
          return;
      }

      return await response.json();
    }
    getId().then((id)=>{
      setLoggedUserId(id)
    })
  }
  async function signUpFunc(name, pass, email) {

    const newUser = { name: name, password: pass, email:email };
    await fetch("http://localhost:5000/user/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newUser),
   })
   .then((d)=>{
     async function getId(){
       const response = await fetch(`http://localhost:5000/user/checkIfUsersDataIsRight/${name}/${pass}`);

       if (!response.ok) {
          const message = `An error occurred at registpage: ${response.statusText}`;
           window.alert(message);
           return;
       }

       return await response.json();
     }
     getId().then((id)=>{
       setLoggedUserId(id)
       navigate(`/profile/${id}`)
     })
   })
   .catch(error => {
     window.alert(error);
     return;
   });
  }
  return(
    <RegistrationPage tryLogIn={logInFunc} trySignUp={signUpFunc} user_link={link}/>
  )
}

export default RegistrationPageContainer
