                                                     {id:3, usersInfo:{name:'Jane', email:'eee@gmail.com', password:'123'}, commentsInfo:{likedComments:[], dislikedComments:[]},markedBooks:[], markedFilms:[], lastReadBook:'', lastWatchedFilm:''}]}
export const addUser = (user) =>{
  dbUsers.users.push(user);
  return [...dbUsers.users];
}

export const getUsersFromDataBase = () =>{
  return [...dbUsers.users];
}

export const updateUser = (user_id, user)=>{
  for (let i=0;i < dbUsers.users.length; i++){
    if (dbUsers.users[i].id == user_id){
      dbUsers.users[i] = user
    }
  }
}
