import s from './catalog.module.css';
import FictionElem from './../Elems/FictionElem/fictionElem.jsx';
import UserElem from './../Elems/UserElem/userElem.jsx';
import React from 'react'
import { useEffect, useState } from 'react';

const CatalogPage = (props) =>{
  const [elems, setElems] = useState([])
  const [searchline, setSearchline] = useState('')

  let allElems = []
  switch (props.type) {
    case 'book':
      allElems = props.elems.map((bookElem)=>{
        return (<FictionElem iD={bookElem.iD} info={bookElem.info} type={props.type}/>)
      });
      break;
    case 'film':
      allElems = props.elems.map((filmElem)=>{
        return (<FictionElem iD={filmElem.iD} info={filmElem.info} type={props.type}/>)
      });
      break;
    case 'user':
      allElems = props.elems.map((userElem)=>{
        return (<UserElem iD={userElem.iD} info={userElem.usersInfo}/>)
      });
      break;
    default:
      break;
  }

  useEffect(()=>{
    let els = []
    for (let i = 0; i < allElems.length; i++) {
      if(allElems[i].info.name.includes(searchline)){
        els.push(allElems[i])
      }
    }
    setElems(els)
  }, [searchline])

  return (
    <div className={s.catalogPage}>
      <div className={s.searchline}>
        <input onChange={(e)=>setSearchline(e)} value={searchline} type="text" />
        <button>Search</button>
      </div>
      <div className={s.elemList}>
        {elems}
      </div>
    </div>
  );
}

export default CatalogPage;
