import s from './comment.module.css'
import {getFictionById} from './../../../DataBase/Fictions.js'
import {getUserById} from './../../../DataBase/Users.js'
import { useEffect, useState } from 'react';
import {useParams} from 'react-router-dom'

const CommentBlock = (props)=>{
  const [author, setAuthor] = useState('')
  const [place, setPlace] = useState('')
  const [isLoaded, setIsLoaded] = useState(false)

  useEffect(()=>{
    let fic = getFictionById(props.comment.staticInfo.place)
    .then((f)=>f.info.name)
    let author = getUserById(props.comment.staticInfo.author)
    .then((u)=>u.usersInfo.name)
    Promise.all([fic, author])
    .thne((res)=>{
      setPlace(res[0])
      setAuthor(res[1])
      setIsLoaded(true)
    })
  }, [props.comment])

  let rating = parseInt(props.comment.dynamicInfo.likes.length) - parseInt(props.comment.dynamicInfo.dislikes.length)

  let like = () =>{
    props.like_or_dislike(props.comment.iD, 'like')
  }
  let dislike = () =>{
    props.like_or_dislike(props.comment.iD, 'dislike')
  }

  let lAndD = <div></div>

  if (props.loggedIn){
    lAndD =  <div className={s.likesAndDislikes}>
                    <button onClick={like}>Like!</button>
                    <div>{rating}</div>
                    <button onClick={dislike}>Dislike!</button>
                  </div>
  } else{
    lAndD =  <div className={s.likesAndDislikes}>
                    <div></div>
                    <div>{rating}</div>
                    <div></div>
                  </div>
  }

  if(isLoaded){
    return (
      <div className={s.commentBlock}>
        <div className={s.names}>
          <div className={s.user}>{props.comment.staticInfo.author}</div>
          <div className={s.placeName} >{props.comment.staticInfo.place}</div>
        </div>
        <div className={s.commentText}>{props.comment.staticInfo.content}</div>
        {lAndD}
      </div>
    )
  } else{
    return <div>Loading</div>
  }


}

export default CommentBlock;
