import logo from './logo.svg';
import './App.css';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import ConstantMenu from './components/ConstantMenu/constantMenu.jsx';
import RegistrationPageContainer from './components/RegistrationPage/registrationPageContainer.jsx'
import ProfilePagesContainer from './components/ProfilePage/profilePagesContainer.jsx'
import CatalogPage from './components/Catalog/catalog.jsx'
import ChosenFictionsContainer from './components/ChosenFiction/chosenFictionsContainer.jsx'
import ChosenFiction from './components/ChosenFiction/chosenFiction.jsx'
import AddFictionPage from './components/AddFictionPage/addFictionPage.jsx'
import {getAllBooks, getAllFilms} from './DataBase/Fictions.js'
import {getAllUsers} from './DataBase/Users.js'
import {getLoggedInStatus} from './LocalInfo/localInfo.js'
import {useState, useEffect} from 'react'

const App = (props) => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);

  let registration = <RegistrationPageContainer dispatch={props.dispatch}/>

  useEffect(()=>{
    let filmsPromise = getAllFilms()
    .then((films)=>films);
    let booksPromise = getAllBooks()
    .then((books)=>books);
    let userPromise = getAllFilms()//users here
    .then((users)=>users);

    Promise.all([filmsPromise, booksPromise, userPromise])
    .then((result)=>{
      setIsLoaded(true);
      setItems(result)
    })

  })

  if(error){
    return <div>Error: {error.message}</div>
  } else if(!isLoaded){
    return (
      <Router>
        <div className="App">
          <ConstantMenu />
          <div className="app-wrapper-content">
            <Routes>
              <Route path='/registration' element={registration}/>
              <Route path='/addFiction' element={<AddFictionPage/>}/>
              <Route path='/5' element={<ChosenFiction fiction={getFictionById(0)} loggedIn={getLoggedInStatus()}
                        usersStatus='not completed'
                        usersRating={5}
                        overallRating={6}
                        comments={[]}
                        fictionAtFriends={[]} addComment={()=>console.log(1)}
                        changeUsersRatingOfFiction={()=>console.log(1)}
                        changeUsersStatusOfFiction={()=>console.log(1)}
                        view={'loggedInUsersView'} dispatch={props.dispatch}/>}/>
            </Routes>
            <ProfilePagesContainer dispatch={props.dispatch}/>
            <ChosenFictionsContainer dispatch={props.dispatch}/>
            </div>
          </div>
        </Router>
      );
  } else if(isLoaded){
    return (
      <Router>
        <div className="App">
          <ConstantMenu />
          <div className="app-wrapper-content">
            <Routes>
              <Route path='/registration' element={registration}/>
              <Route path='/addFiction' element={<AddFictionPage/>}/>
              <Route path='/filmCatalog' element={<CatalogPage type='film' elems={items[0]}/>}/>
              <Route path='/bookCatalog' element={<CatalogPage type='book' elems={items[1]}/>}/>
              <Route path='/userCatalog' element={<CatalogPage type='film' elems={items[2]}/>}/>
            </Routes>
            <ProfilePagesContainer dispatch={props.dispatch}/>
            <ChosenFictionsContainer dispatch={props.dispatch}/>
            </div>
          </div>
        </Router>
      );
  }

}

export default App;
