import s from './atFollowed.module.css'
import { getUsersFollowedPeople } from './../../../DataBase/Users.js'
import { NavLink, useNavigate, useParams } from "react-router-dom";
import { useState, useEffect } from "react";

const AtFollowed = (props) => {
  const [fictionAtFollowed, setFictionAtFollowed] = useState([])
  const [amountOfPages, setPages] = useState(1)
  const [page, setPage] = useState(0)
  const [followedElems, setFollowedElems] = useState([])
  const [isLoaded, setIsLoaded] = useState(false)
  const [error, setError] = useState(null)

  const navigate = useNavigate()
  const params = useParams()

  useEffect(()=>{
    getUsersFollowedPeople(props.userID)
    .then((people)=>{
      let k = ''
      let miniPromises = []
      for (let i = 0; i < people.length; i++) {
        k = getFictionAtUser(people[i].iD, params.iD)
        .then((status)=>{

          let d = {iD:people[i].iD, name:people[i].usersInfo.name, status:status}
          return d
        })
        miniPromises.push(k)
      }
       return Promise.all(miniPromises)
    })
    getUsersFollowedPeople(props.userID)
    .then((people)=>{
      setFictionAtFollowed(people)
      setPages((people.length%6 == 0) ? people.length/6 : (people.length-(people.length%6))/6+1)
      setFollowedElems(people.slice((page)*6, (page)*6 + 6).map((f)=>{ //
       let link_to_friend = '/profile/' + f.iD
       return (
         <NavLink to={link_to_friend}>
         <div className={s.friendElem}>
           <div>{f.name}</div>
           <div>{f.status}</div>
         </div>)
         </NavLink>)
     }))
     console.log('damn');
      setIsLoaded(true)
    }, (error)=>{
      setError(error)
    })
 }, [params])

 let forward = ()=>{
   if (page < amountOfPages-1){
     setPage(page+1)
     setFollowedElems(followedPeople.slice((page+1)*6, (page+1)*6 + 6).map((f)=>{ //
      let link_to_friend = '/profile/' + f.iD
      return (
        <NavLink to={link_to_friend}>
        <div className={s.friendElem}>
          <div><img src={f.usersInfo.profilePhoto}/></div>
          <div>{f.usersInfo.name}</div>
        </div>
        </NavLink>)
    }))
   }
 }
 let back = ()=>{
   if (page > 0){
     setPage(page-1)
     setFollowedElems(followedPeople.slice((page-1)*6, (page-1)*6 + 6).map((f)=>{ //
      let link_to_friend = '/profile/' + f.iD
      return (
        <NavLink to={link_to_friend}>
        <div className={s.friendElem}>
          <div><img src={f.usersInfo.profilePhoto}/></div>
          <div>{f.usersInfo.name}</div>
        </div>
        </NavLink>)
    }))
   }
 }

 if (error){
   return (
     <div className={s.friend_list}>
       <div>Fiction at followed</div>
       <div className={s.list_of_friends}>
         Error: {error.message}
       </div>
       <div className={s.pagesNButtons}>
           <button onClick={back}>Back</button>
           <div>{page+1}/{amountOfPages}</div>
           <button onClick={forward}>Forward</button>
       </div>

   </div>
   )
 } else if (!isLoaded) {
   return (
     <div className={s.friend_list}>
       <div>Fiction at followed</div>
       <div className={s.list_of_friends}>
         Loading...
       </div>
       <div className={s.pagesNButtons}>
           <button onClick={back}>Back</button>
           <div>{page+1}/{amountOfPages}</div>
           <button onClick={forward}>Forward</button>
       </div>

   </div>
   )
 } else if (isLoaded) {
   return (
     <div className={s.at_friends}>
       <div>Fiction at followed</div>
       <div className={s.list_of_friends}>
         {followedElems}
       </div>
       <div className={s.pagesNButtons}>
         <button onClick={back}>Back</button>
         <div>{(amountOfPages == 0)? page: page+1}/{amountOfPages}</div>
         <button onClick={forward}>Forward</button>
       </div>
     </div>
   )
 }

}

export default AtFollowed;
