import logo from './logo.svg';
import './App.css';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import ConstantMenu from './components/ConstantMenu/constantMenu.jsx';
import RegistrationPageContainer from './components/RegistrationPage/registrationPageContainer.jsx'
import ProfilePagesContainer from './components/ProfilePage/profilePagesContainer.jsx'
import CatalogPage from './components/Catalog/catalog.jsx'
import ChosenFictionsContainer from './components/ChosenFiction/chosenFictionsContainer.jsx'
import AddFictionPage from './components/AddFictionPage/addFictionPage.jsx'
import {getAllBooks, getAllFilms} from './DataBase/Fictions.js'
import {getAllUsers} from './DataBase/Users.js'
import {getLoggedInStatus} from './LocalInfo/localInfo.js'

const App = (props) => {

  let registration = <RegistrationPageContainer dispatch={props.dispatch}/>

  let route_to_film_catalog = ''
  let route_to_book_catalog = ''
  let route_to_user_catalog = ''
  let filmsPromise = getAllFilms()
  .then((films)=><Route path='/filmCatalog' element={<CatalogPage type='film' elems={films}/>}/>)
  let booksPromise = getAllBooks()
  .then((books)=><Route path='/bookCatalog' element={<CatalogPage type='film' elems={books}/>}/>)
  let userPromise =  getAllFilms()//users here
  .then((films)=><Route path='/userCatalog' element={<CatalogPage type='film' elems={films}/>}/>)

  Promise.all(filmsPromise, booksPromise, userPromise)
  .then(values => {
      return (
        <Router>
          <div className="App">
            <ConstantMenu />
            <div className="app-wrapper-content">
              <Routes>
                <Route path='/registration' element={registration}/>
                <Route path='/addFiction' element={<AddFictionPage/>}/>
                {values[0]}
                {values[0]}
                {values[0]}
                </Routes>
                <ProfilePagesContainer dispatch={props.dispatch}/>
                <ChosenFictionsContainer dispatch={props.dispatch}/>
              </div>
            </div>
          </Router>
        );
  })
}

export default App;
