const LOG_IN = 'LOG_IN';
const SIGN_UP = 'SIGN-UP';
const ADD_LIKED_OR_DISLIKED_COMMENT = 'ADD_LIKED_OR_DISLIKED_COMMENT';

let dataBase = {users:[{usersInfo:{name:'John', email:'eee@gmail.com', password:'lol'}, commentsInfo:{likedComments:[], dislikedComments:[]},markedBooks:[], markedFilms:[], lastReadBook:'', lastWatchedFilm:''},
                                                     {usersInfo:{name:'Paul', email:'eee@gmail.com', password:'456'}, commentsInfo:{likedComments:[], dislikedComments:[]},markedBooks:[], markedFilms:[], lastReadBook:'', lastWatchedFilm:''},
                                                     {usersInfo:{name:'Jane', email:'eee@gmail.com', password:'123'}, commentsInfo:{likedComments:[], dislikedComments:[]},markedBooks:[], markedFilms:[], lastReadBook:'', lastWatchedFilm:''}]}

let intialState = {status:'-', loggedUser:'', users: [...dataBase.users]};

const usersReducer = (state = intialState, action) => {
  switch (action.type) {
    case LOG_IN:{
      for (let i=0; i<state.users.length;i++){
        if ((state.users[i].usersInfo.name == action.username) && (state.users[i].usersInfo.password == action.password)){
          let stateCopy = {...state}
          stateCopy.loggedUser = state.users[i]
          stateCopy.status = '+';
          return stateCopy;
        }
      }

      return state;
    }
    case SIGN_UP:{
      for (let i=0; i<state.users.length;i++){
        if (state.users[i].usersInfo.name == action.username){
          return state;
        }
      }
      let stateCopy = {...state}
      stateCopy.users = [...state.users]

      let newUser = {usersInfo:{name:action.username, email:action.email, password:action.password}, commentsInfo:{likedComments:[], dislikedComments:[]},markedBooks:[], markedFilms:[], lastReadBook:'', lastWatchedFilm:''}

      stateCopy.users.push(newUser)

      stateCopy.loggedUser = stateCopy.users[stateCopy.users.length-1]
      stateCopy.status = '+';
      console.log(stateCopy.users);
      return stateCopy;
    }
    case ADD_LIKED_OR_DISLIKED_COMMENT:{
      let stateCopy = {...state}
      if (action.like_or_dislike == 'like'){
        stateCopy.loggedUser.commentsInfo.likedComments = [...stateCopy.loggedUser.commentsInfo.likedComments]
        stateCopy.loggedUser.commentsInfo.likedComments.push(action.comment)
        console.log(stateCopy.loggedUser.commentsInfo.likes)
      }else if (action.like_or_dislike == 'dislike'){
        stateCopy.loggedUser.commentsInfo.dislikedComments = [...stateCopy.loggedUser.commentsInfo.dislikedComments]
        stateCopy.loggedUser.commentsInfo.dislikedComments.push(action.comment)
        console.log(stateCopy.loggedUser.commentsInfo.dislikedComments)
      }
      return stateCopy;
    }

    default:
      return state;
  }
};

export default usersReducer;

export const tryLogIn = (username, password) => (
  {type: LOG_IN, username:username, password:password});
export const trySignUp = (username, password, email) => (
  {type: SIGN_UP, username:username, password:password, email:email});
  export const likeOrDislikeCommentActionCreator = (comment, like_or_dislike) => (
    {type: ADD_LIKED_OR_DISLIKED_COMMENT, comment:comment, like_or_dislike:like_or_dislike});
