import s from './filmElem.module.css';
import {NavLink} from "react-router-dom";

const FilmElem = (props) =>{
  let filmLink = '/filmCatalog/' + props.info.id;
  return (
    <div className={s.filmElem}>
      <div className={s.filmCover}><img src={props.film.info.coverPhoto} /></div>
      <div className={s.details}>
        <h3>{props.film.info.name}</h3>
        <p><b>Genre:</b> {props.film.info.genre}</p>
        <p>{props.film.info.description}</p>
      </div>
      <div className={s.rating}>Rating:<br />{props.film.overallRating}</div>
      <div className={s.link_button}><NavLink to={filmLink}><button>Look!</button></NavLink></div>
    </div>
  )
}

export default FilmElem;
