import {addUser, getAllUsers} from './../../DataBase/Users.js'
import {setLoggedUserId} from './../../LocalInfo/localInfo.js'

const LOG_IN = 'LOG-IN';
const SIGN_UP = 'SIGN-UP';

let initialState = []

const registrationReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOG_IN:{
      let users = getAllUsers()
      for (var i = 0; i < users.length; i++) {
        if (users[i].usersInfo.name == username && users[i].usersInfo.password == password){
          setLoggedUserId(action.user_id)
        }
      }
      return state
    }
    case SIGN_UP:{
      let users = getAllUsers()
      for (var i = 0; i < users.length; i++) {
        if (users[i].usersInfo.email == email){
          addUser
        }
        if (users[i].usersInfo.username == username){
          return state
        }
      }
      setLoggedUserId(users[users.length-1].id)
      return state
    }

    default:
      return state;
  }
};

export default registrationReducer;

export const tryLogIn = (username, password) => (
  {type: LOG_IN, username, password});
export const trySignUp = (username, password, email) => (
  {type: SIGN_UP, username:username, password:password, email:email});
