import s from './bookElem.module.css'
import {NavLink} from "react-router-dom";

const FictionElem = (props) =>{
  let fictionLink = '';
  if (props.type == 'book'){
    fictionLink = '/bookCatalog/' + props.fiction.id;
  } else if (props.type == 'film'){
    fictionLink = '/filmCatalog/' + props.fiction.id;
  }
  return (
    <div className={s.fictionElem}>
      <div className={s.coverPhoto}><img src={props.fiction.info.coverPhoto} /></div>
      <div className={s.details}>
        <h3>{props.fiction.info.name}</h3>
        <p><b>Genre:</b> {props.fiction.info.genre}</p>
        <p>{props.fiction.info.description}</p>
      </div>
      <div className={s.rating}>Rating:<br />{props.fiction.overallRating}</div>
      <div className={s.link_button}><NavLink to={fictionLink}><button>Look!</button></NavLink></div>
    </div>
  )
}

export default FictionElem;
