import s from './followList.module.css'
import { NavLink, useNavigate } from "react-router-dom";
import { useState } from "react";



const FollowList = (props) =>{
  let arr = [{usersInfo:{profilePhoto:'', name:'Bob'}},{usersInfo:{profilePhoto:'', name:'Bob'}},{usersInfo:{profilePhoto:'', name:'Bob'}},{usersInfo:{profilePhoto:'', name:'Bob'}},{usersInfo:{profilePhoto:'', name:'Bob'}},{usersInfo:{profilePhoto:'', name:'Bob'}}]

  const [amountOfPages] = (arr.length%6 == 0) ? arr.length/6 : (arr.length-(arr.length%6))/6
  console.log(amountOfPages);

  const navigate = useNavigate()

  let lff_b = ''
  if (props.view == 'loggedUsersProfile'){
    lff_b = <button onClick={()=>navigate('/catalog/allUsers')}>Look for people!</button>
  } else {
    lff_b = <div></div>
  }


  let followedElems = arr.map((f)=>{ //props.followedPeople
    let link_to_friend = '/profile/' + f.iD
    return (
      <NavLink to={link_to_friend}>
      <div className={s.friendElem}>
        <div><img src={f.usersInfo.profilePhoto}/></div>
        <div>{f.usersInfo.name}</div>
      </div>
      </NavLink>)
  })

  return (
    <div className={s.friend_list}>
      <div>Followlist</div>
      <div className={s.list_of_friends}>
        {followedElems}
      </div>
      <div className={s.pagesNButtons}>
        <div className={s.bf}>
          <button>Back</button>
          <div>1/1</div>
          <button>Forward</button>
        </div>
        <div className={s.lff}>{lff_b}</div>
      </div>

  </div>
  );
}

export default FollowList;
